nta.declaration:
urgent broadcast chan bounce;
broadcast chan hit;
clock time, p, v;
const double G = 9.81;
const bool lazy = true;

/** Statistics */
int number_deaths = 0;
int swings = 0;
int hits = 0;

/** Utility functions */
double round_8(double x) { return round(x * 1e8)/1e8; }
int canHit(double p, double v) { return (p > 4 && v > -4) ? 1 : 0; }
int p_thresh(double p) { return round_8(p) > 4 ? 1 : 0; }


/** Memory */
const int MEM_SIZE = 5;
double memory[MEM_SIZE];
int newestMemory = -1;

// Update memory array and pointers
void updateMemory() {
    newestMemory++;
    if (newestMemory >= MEM_SIZE) { newestMemory = 0; }
    memory[newestMemory] = p;
}

// Set all memory values to zero and pointers to -1
void resetMemory() {
    int i;
    newestMemory = -1;
    for (i = 0; i < MEM_SIZE; i++) { memory[i] = 0.0; }
}

// Get ith memory (0 is the newest)
double getMemory(int i) {
    return memory[(newestMemory + i) % MEM_SIZE];
}

/** Energy */
double potEnergy(double p) { return G * p; }
double kinEnergy(double v) { return 0.5 * v * v; }
double mecEnergy(double p, double v) { return kinEnergy(v) + potEnergy(p); }

// Energy gained by hitting the ball
double delta_e_mek(double v, double p) {
    double e_mek = mecEnergy(p, v);
    if (v < -4 || p < 4)  { return 0; }

    if (v < 0) { v = -4; }
    else       { v = -4 - 0.95 * v; }

    return mecEnergy(p, v) - e_mek;
}


/** Shielding */

// [ SHIELD_PATH
