const bool shield_enabled = // [ ENABLE_SHIELD
    true
// ]
;

// Shield decisions
const int NOTHING = 0, ONLY_HIT = 1, DONT_HIT = 2, EVERYTHING = 3;

// Struct to register allowed actions
typedef struct {
    bool hit;
    bool nohit;
} allowedActions_t;
allowedActions_t allowed;
allowedActions_t trueAllowed;
int trueDecision;

double trueMechE, trueV, truepThresh;

// Update allowed actions
void updateAllowed() {
    int decision = // [ SHIELD_CALL
        shield(mecEnergy(p, v), v, p_thresh(p))
    // ]
    ;

    bool hit   = (decision == ONLY_HIT || decision == EVERYTHING);
    bool nohit = (decision == DONT_HIT || decision == EVERYTHING);
    allowedActions_t trueAllowedtemp = { hit, nohit };
    trueAllowed = trueAllowedtemp;
    trueDecision = decision;

    trueMechE = round_8(mecEnergy(p, v));
    trueV = round_8(v);
    truepThresh = p_thresh(p);

    if (!hit && !nohit) { allowedActions_t result = { true, true }; allowed = result; }
    else                { allowedActions_t result = { hit, nohit }; allowed = result; }
}

